package string;

public class CharAt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1 = "Merit Campus";
        char c1 = s1.charAt(0); //LINE A
        System.out.println(c1);
        char c2 = s1.charAt(4);  //LINE B
        System.out.println(c2); 
	}

}
